jQuery(document).ready(function($){var current_rating_reportupload=0;var frame=wp.media({title:RATING_REPORT_MEDIA.text_title,multiple:false,library:{type:"image"},button:{text:RATING_REPORT_MEDIA.text_button}});rating_reportopen_uploader=function(id){current_rating_reportupload=id;frame.open();return false};frame.on("close",function(){var attachment=frame.state().get("selection").first().toJSON();rating_reportrender_image(attachment)});rating_reportrender_image=function(attachment){img_html='<img src="'+attachment.url+'" ';img_html+='width="'+attachment.width+'" ';img_html+='height="'+attachment.height+'" ';if(attachment.alt!=""){img_html+='alt="'+attachment.alt+'" '}img_html+=">";$("#"+current_rating_reportupload+"_image").attr("src",attachment.url).attr("width",attachment.width).attr("height",attachment.height).attr("srcset",attachment.url+" "+attachment.width+"w").show();$("#"+current_rating_reportupload).val(attachment.id);$("#"+current_rating_reportupload+"_remove").show()};rating_reportclear_uploader=function(current_rating_reportupload){$("#"+current_rating_reportupload+"_image").attr("src","").hide();$("#"+current_rating_reportupload).val("");$("#"+current_rating_reportupload+"_remove").hide();return false}});